import './ExpenseForm.css';
import { useForm, userForm } from 'react-hook-form';
import { zodResolver } from "@hookform/resolvers/zod";
import { string, z } from "zod";

const schema = z.object({
    title: string(),
    amount: string(),
    date: string()
})

function ExpenseForm () {

    const { register, control, handleSubmit, formState } = useForm({resolver: zodResolver(schema)});

    const onSubmit = (data) => {
        // e.preventDefault();
        console.log(data);
    }

    const onError = () => {
        // e.preventDefault();
        console.log('Wrong');
    }

    const errors = {};

    return (
        <form onSubmit={handleSubmit(onSubmit, onError)}>
            <div className='new-expense__controls'>
                <div className="new-expense__controls">
                    <label>Title</label>
                    <input type='text' {...register('title')} />
                    <div style={{ color: "red" }}>{errors.name?.message}</div>
                </div>
                <div className='new-expense__controls'>
                    <label>Amount</label>
                    <input type='number' {...register('amount')} min='0.01' step='0.01'/>
                    <div style={{ color: "red" }}>{errors.amount?.message}</div>
                </div>
                <div className='new-expense__controls'>
                    <label>Date</label>
                    <input type='date' {...register('date')} min='2019-01-01' max='2022-12-31'/>
                    <div style={{ color: "red" }}>{errors.date?.message}</div>
                </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>
            </div>
        </form>
    )
}

export default ExpenseForm;
